// the relational operators are <, <=, >, >=, ==, !=

void relation_operators() {
    int a = 1;
    int b = 2;

    bool c = a < b;
    bool d = a <= b;
    bool e = a > b;
    bool f = a >= b;
    bool g = a == b;
    bool h = a != b;
}

void logical_operators() {
    bool a = true;
    bool b = false;

    bool c = a || b;
    bool d = a && b;
}

void arithemtic_operators() {
    int a = 1;
    int b = 2;

    int c = a + b;
    int d = a - b;
    int e = a * b;
    int f = a / b;
}

void unary_arithmetic_operators() {
    int a;

    a++;
    ++a;
    a--;
    --a;
}
